import { Box, Button, Center, Input, Text } from "@chakra-ui/core";
import { NextPage } from "next";
import React, { FC, useEffect, useState } from "react";

interface Credentials {
    username: string;
    password: string;
}

interface SignInputProps {
    label: string;
    placeholder: string;
}

const SignInInput: FC<SignInputProps> = ({ label, placeholder }: SignInputProps) => {
    return (
        <Box my={3}>
            <Text fontSize={"sm"}>{label}</Text>
            <Input placeholder={placeholder} />
        </Box>
    );
};

const IndexPage: NextPage = () => {
    const [bgColor, setBgColor] = useState<string>("red");
    const [message, setMessage] = useState<string>("Hello World");

    const [credentials, setCredentials] = useState<Credentials>({ username: "", password: "" });

    useEffect(() => {
        console.log("I'm rendering");
    }, [message]);

    const onClick = () => {
        setMessage(message.split(" ").reverse().join(" "));
        setBgColor(bgColor === "red" ? "green" : "red");
    };

    return (
        <Center w="100vw" h="100vh" position="absolute" top={0} left={0}>
            <Box px={6} py={10} w={1 / 4} bg={"gray.50"}>
                <Text fontSize={"3xl"}>Sign In</Text>

                <SignInInput label={"Username"} placeholder={"Ingrese su username"} />
                <SignInInput label={"Contrase単a"} placeholder={"Ingrese su contrase単a"} />

                <Button mt={2} bg={"orange.300"}>
                    Ingresar
                </Button>
            </Box>
        </Center>
    );
};

export default IndexPage;
